<script setup>

import { defineProps } from 'vue';
import store from "@/store/store.js"
const props = defineProps(['avantages','item'])

const secDEc = ref(false)

const showDescription = (item) => {
  let choosenItem = props.item.index
  store.commit('isOpenAvantage',choosenItem)
  item.isOpen = true
  setTimeout(() => {
    secDEc.value = true
  },150);

}


const changeIsOpenStatus = (itemIndex) =>{
  store.commit('setAvantageImageStatus',{itemInd:itemIndex,status:true })
}

const choosenAvantage = computed(() => {
  return store.getters.getChoosenAvantage;
});


const setChoosenAvantage = () => {
  store.commit('setAvantage', props.item.index)
}




const hideDescription = (itemIndex) => {
  store.commit('setAvantageImageStatus',{itemInd:itemIndex,status:false })
  store.commit('removeAvantage',itemIndex)
  props.item.isOpen = false
  setTimeout(() => {
    secDEc.value = false
  },150);
}


</script>


<style>
.descriptionParam{
  height:50px;
  transition: all;
  transition: ease-in-out;
  transition-duration: 200ms;
}


</style>